mapper.select:
SELECT CH.*, UD.TYPES FROM
( SELECT C.* FROM (SELECT CAST(@RNUM := @RNUM + 1 AS SIGNED INTEGER) AS NO, A.* FROM ( SELECT AL.ALBUM_TITLE, AR.ARTIST_NAME, MUS.MUSIC_TITLE, MUS.MUSIC_SEQ, AL.ALBUM_SEQ, AR.ARTIST_SEQ, MUS.GENRE_SEQ, DATE_FORMAT(V.VIEW_DATE,'%y-%m-%d')AS VIEW_DATE, COUNT(SEQ_GROUP)VIEW_COUNT, ( SELECT CONCAT(IMG_NAME, '.', EXT) FROM IMG WHERE SEQ LIKE AL.ALBUM_SEQ) IMG FROM MUSIC MUS JOIN VIEW_RECORD V ON V.SEQ_GROUP LIKE MUS.MUSIC_SEQ JOIN ALBUM AL ON MUS.ALBUM_SEQ LIKE AL.ALBUM_SEQ JOIN ARTIST AR ON MUS.ARTIST_SEQ LIKE AR.ARTIST_SEQ
WHERE VIEW_DATE BETWEEN CONCAT(#{date1},'%') AND CONCAT(#{date2},'%')
GROUP BY V.SEQ_GROUP ORDER BY VIEW_COUNT DESC LIMIT 50) AS A, (SELECT @RNUM := 0) AS B) AS C WHERE C.NO BETWEEN #{PageNo} AND #{PageNoEnd}
)CH LEFT JOIN (SELECT * FROM( SELECT MAX(U.UD_SEQ) MAX_SEQ FROM UPDOWN U WHERE MEMBER_ID LIKE #{id} GROUP BY U.SEQ_GROUP ORDER BY U.UD_SEQ DESC )A JOIN(SELECT * FROM UPDOWN U) B ON A.MAX_SEQ LIKE B.UD_SEQ ORDER BY B.SEQ_GROUP)UD ON CH.MUSIC_SEQ LIKE UD.SEQ_GROUP GROUP BY CH.MUSIC_TITLE ORDER BY NO
